import minify from 'rollup-plugin-babel-minify';
import resolve from 'rollup-plugin-node-resolve';
import fs from 'fs';

const pkgName = 'lit-component';
const pkg = JSON.parse(fs.readFileSync('./package.json'));
const external = Object.keys(pkg.dependencies || {});
const input = 'src/index.js';

export default [
  {
    input, external,
    output: [
      {
        file: `dist/${pkgName}.es.js`,
        format: 'es',
        name: 'LitComponent'
      }
    ],
    plugins: [resolve()]
  },
  {
    input, external,
    output: [
      {
        file: `dist/${pkgName}.es.min.js`,
        format: 'es',
        name: 'LitComponent'
      }
    ],
    plugins: [resolve(), minify({ comments: false })]
  },
  {
    input,
    output: [
      {
        file: `dist/${pkgName}.js`,
        format: 'iife',
        name: 'LitComponent'
      },
      {
        file: `dist/${pkgName}.umd.js`,
        format: 'umd',
        name: 'LitComponent'
      }
    ],
    plugins: [resolve()]
  },
  {
    input,
    output: [
      {
        file: `dist/${pkgName}.min.js`,
        format: 'iife',
        name: 'LitComponent'
      },
      {
        file: `dist/${pkgName}.umd.min.js`,
        format: 'umd',
        name: 'LitComponent'
      }
    ],
    plugins: [resolve(), minify({ comments: false })]
  }
]